import React, { useEffect, useState } from 'react';
import SockJS from 'sockjs-client';
import Stomp from 'stompjs';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [stompClient, setStompClient] = useState(null);

  useEffect(() => {
    // Подключение к WebSocket
    const socket = new SockJS('http://localhost:8082/chat');
    const client = Stomp.over(socket);
    client.connect({}, () => {
      client.subscribe('/topic/messages', (message) => {
        const newMessage = JSON.parse(message.body);
        setMessages((prev) => [...prev, newMessage]);
      });
    });
    setStompClient(client);

    return () => client.disconnect();
  }, []);

  const sendMessage = () => {
    if (stompClient && input) {
      const message = { sender: 'user', content: input };
      stompClient.send('/app/sendMessage', {}, JSON.stringify(message));
      setInput('');
    }
  };

  return (
    <div className="messenger">
      <div className="sidebar">
        <h2>Чаты</h2>
        <div className="chat-list">
          <div className="chat-item">Иван Иванов</div>
        </div>
      </div>
      <div className="main">
        <div className="chat-header">Иван Иванов</div>
        <div className="messages">
          {messages.map((msg, index) => (
            <div key={index} className={`message ${msg.sender === 'user' ? 'sent' : 'received'}`}>
              {msg.content}
            </div>
          ))}
        </div>
        <div className="input-area">
          <textarea
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Напишите сообщение..."
          />
          <button onClick={sendMessage}>Отправить</button>
        </div>
      </div>
    </div>
  );
}

export default App;
